mapper.insert:
insert into chatmessage (
	message_id,    tenant_id,     message_type,      receiver_id,      receiver_type,     sender_id,         content,
	cont_type,     file_name,     file_path,         created_time,     user_image,        sender_name
)
values (
	#{messageId},  #{tenantId},   #{messageType},    #{receiverId},    #{receiverType},   #{senderId},       #{content},
	#{contType},   #{fileName},   #{filePath},       #{createdTime},   #{userImage},      #{senderName}
)

insert into conversation (
	user_id,     related_id,      last_message,      lastChated,       tenant_id
)
values (
	#{userId},   #{relatedId},    #{lastMessage},    #{lastChated},    #{tenantId}
)
on duplicate key update
	last_message = #{lastMessage},
	lastChated   = #{lastChated}
